import java.util.*;

public class Playground {

    public void run(){
        boolean asdf = judgeCircle("");
        System.out.print("asdf");
    }

    public boolean judgeCircle(String moves) {
        
        if(moves.equals(""))
            return true;

        char[] charArray = moves.toCharArray();
        Map<Character,Integer> charCount = new HashMap<>();
        charCount.put('L',0);
        charCount.put('R',0);
        charCount.put('U',0);
        charCount.put('D',0);

        //populate map
        for(Character character : charArray){
            Integer tempCount = charCount.get(character);
            charCount.put(character, ++tempCount);
        }

        return(charCount.get('L') == charCount.get('R') && charCount.get('U') == charCount.get('D'));
    }

}
